import News37 from '../media/News7.jpg';
import News38 from '../media/News8.jpg';
import News39 from '../media/News9.jpg';

const allNewsPics = {
		0: `${News4}`,
		1: `${News1}`,
		2: `${News2}`,
		3: `${News3}`,
		4: `${News4}`,
		5: `${News5}`,
		6: `${News6}`,		
		7: `${News1}`,
		8: `${News2}`,
		9: `${News3}`,
		10: `${News4}`,
		11: `${News1}`,
		12: `${News2}`,
