		35: `${News5}`,
		36: `${News6}`,		
		37: `${News1}`,
		38: `${News2}`,
		39: `${News3}`,
			
	}
    
class Gallery extends Component {
  constructor(props) {
    super(props);
    this.state = {
      items: 10,
      hasMoreItems: true,
      threshold: 50
    };
  }

  
 
  showItems() {
    var items = [];
    var w = ((window.innerWidth / 4) - 5);
		for (var i = 0; i < this.state.items; i++) {
      items.push(
      	<img 
    			style={{
     				width: `${w}px`, /* width of container */
   					height: `${w}px`, /* height of container */
   					objectFit: "cover",
   					margin: "auto",
   					padding: "10px"
     			}} 
     			src={allNewsPics[i]} 
     			alt="News Image" 
      	/>
      );
    }
    return items;
  }
    
  loadMore() {
    if(this.state.items===40){
      this.setState({ hasMoreItems: false});
    }else{
      setTimeout(() => {
        this.setState({ items: this.state.items + 1});
      }, 100);
    }       
  }
    
  render() {
    return (
      <div>
        <div>
          <InfiniteScroll
            loadMore={this.loadMore.bind(this)}
            hasMore={this.state.hasMoreItems}
            loader={<div>Loading...</div>}
            useWindow={true}
            threshold={this.state.threshold}
          >

            {this.showItems()}{" "}
          </InfiniteScroll>{" "}
        </div>
      </div>
    );
  }
}
    
export default Gallery;



