import React from 'React';
import news1 from './media/News1.jpeg';


const NewsPicture = (mediaPic) => {
	const picture= "";
	if (mediaPic = news1) {
		picture = {news1}
		} else if (mediaPic = news2) {
		picture = "{news2}"
		}
	
	return(
		<div className="ui small image">
			<img src={picture} />
		</div>
	);
	
};

export default NewsPicture;
