		30: `${News4}`,
		31: `${News1}`,
		32: `${News2}`,
		33: `${News3}`,
		34: `${News4}`,
		35: `${News5}`,
		36: `${News6}`,		
		37: `${News1}`,
		38: `${News2}`,
		39: `${News3}`,
			
	}
    
class Gallery extends Component {
  constructor(props) {
    super(props);
    this.state = {
      items: 10,
      hasMoreItems: true
    };
  }

  
 
  showItems() {
    var items = [];
    for (var i = 0; i < this.state.items; i++) {
      items.push(<div className="ui medium image"> <img src={allNewsPics[i]} alt="News Image" /> </div>);
    }
    return items;
  }
    
  loadMore() {
    if(this.state.items===40){
      this.setState({ hasMoreItems: false});
    }else{
      setTimeout(() => {
        this.setState({ items: this.state.items + 1});
      }, 100);
    }       
  }
    
  render() {
    return (
      <div className="App">
        <div style={{height:'1000px', overflow:'auto'}}>
          <InfiniteScroll
            loadMore={this.loadMore.bind(this)}
            hasMore={this.state.hasMoreItems}
            loader={<div className="loader"> Loading... </div>}
            useWindow={false}
          >
            {this.showItems()}{" "}
          </InfiniteScroll>{" "}
        </div>{" "}
      </div>
    );
  }
}
    
export default Gallery;



