import React, { useState } from 'react';
import picture from './media/Headshot.jpg'

const MediaItem = ({ items }) => {
	
	//start new
	const [activeIndex, setActiveIndex] = useState(null);
	const [Item, setItem] = useState({});

	const itemOpened = [];

	const onTitleClick = (index) => {
		setActiveIndex(index);
	};

	const onReadMoreClick = (index, itemOpened) => {
		setActiveIndex(index);

	};

	const renderedItems = items.map((item, index, itemOpened) => {

		const active = index === activeIndex ? 'active' : '';
		const contentString = index === activeIndex ? `${item.content}` : `${item.content.slice(0, 50)}...`;
		const hideButton = index === activeIndex ? 'hidden' : ''; 

		return <React.Fragment key={item.title}> 
			
			<div className="item">
				<div className="ui small image">
					<img src={picture} />
				</div>
				<div className="middle aligned content">
					<div className="header">
						{item.title}
					</div>
					<div className="description">
						{item.content}
					</div>
					<div className="extra">
						<div className="ui tiny right floated blue basic button">
							Read More
						</div>
					</div>

				</div>
			</div>
		

		</React.Fragment>
	});

	return <div className="ui items">
		{renderedItems}
	</div>
};

export default MediaItem;
