import News36 from '../media/News6.jpg';
import News37 from '../media/News7.jpg';
import News38 from '../media/News8.jpg';
import News39 from '../media/News9.jpg';

    
class Gallery extends Component {
  constructor(props) {
    super(props);
    this.state = {
      items: 10,
      hasMoreItems: true
    };
  }
 
  showItems() {
    var items = [];
    for (var i = 0; i < this.state.items; i++) {
      items.push(/*<li key={i}>`${News1}` </li>*/<div className="ui medium image"> <img src={News1} alt="News Image" /> </div>);
    }
    return items;
  }
    
  loadMore() {
    if(this.state.items===40){
          
      this.setState({ hasMoreItems: false});
    }else{
      setTimeout(() => {
        this.setState({ items: this.state.items + 1});
      }, 100);
    }       
  }
    
  render() {
    return (
      <div className="App">
        <div style={{height:'1000px', overflow:'auto'}}>
          <InfiniteScroll
            loadMore={this.loadMore.bind(this)}
            hasMore={this.state.hasMoreItems}
            loader={<div className="loader"> Loading... </div>}
            useWindow={false}
          >
            {this.showItems()}{" "}
          </InfiniteScroll>{" "}
        </div>{" "}
      </div>
    );
  }
}
    
export default Gallery;



