import React, { useState } from 'react';

const MediaItem = ({ items }) => {
	
	//start new
	const [activeIndex, setActiveIndex] = useState(null);

	const onTitleClick = (index) => {
		setActiveIndex(index);
	};

	const renderedItems = items.map((item, index) => {
		const active = index === activeIndex ? 'active' : '';
		const contentString = index === activeIndex ? `${item.content}` : `${item.content.slice(0, 20)}...`;
		const hideButton = index === activeIndex ? 'hidden' : '';
		return <React.Fragment key={item.title}> 
			<div className="ui styled accordion">
				<div 
					className={`title ${active}`}
					onClick={() => onTitleClick(index)}
				> 
					<div className="ui header">
						{item.title}
					</div>
					{item.date}
				</div>
				<div className="ui segment">
					{contentString}
					<div 
						className="ui tiny blue basic button" 
						style={{display: "inlineBlock", marginLeft: "40px" }}
						onClick={() => onTitleClick(index)}
					>
						Read More
					</div>
				</div>
				
			</div>
			
		</React.Fragment>
	});

	return <div className="">
		{renderedItems}
	</div>
};

export default MediaItem;
