import React, { useState } from 'react';
import NewsPic from './NewsPic';
import News1 from './media/News1.jpg';


const MediaItem = ({ items }) => {
	
	//start new
	const [activeIndex, setActiveIndex] = useState(null);
	const [Item, setItem] = useState({});

	const itemOpened = [];

	const onTitleClick = (index) => {
		setActiveIndex(index);
	};

	const onReadMoreClick = (index, itemOpened) => {
		setActiveIndex(index);

	};

	const allNewsPics = {
		1: {News1},
		2: {News2},
		3: {News1},
		4: {News1},
		5: {News1},
		6: {News1}

	}

	const NewsPic = (index) => {
		if (index === 1) {
			return (
				<div className="ui small image">
					<img src={News1}/>
				</div>
			);
		} 
	};


	const renderedItems = items.map((item, index, itemOpened) => {



		return <React.Fragment key={item.title}> 
			<div className="item">
					<div className="ui small image">
						<img src={allNewsPics[item.id]} alt="News Image" />
					</div>			
				<div className="middle aligned content">
					<div className="header">
						{item.title}
					</div>
					<div className="description">
						{item.content}
					</div>
					<div className="extra">
						<div className="ui right floated blue basic button">
							Read More
						</div>
					</div>
				</div>
			</div>
			<div className="ui divider"></div>
		</React.Fragment>
	});

	return <div className="ui items">
		{renderedItems}
	</div>
};

export default MediaItem;
